import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";
import { GameBoard } from "./components";

class App extends Component {
  state = {
    puzzle: [],
    columns: [],
    isLoading: true
  };

  componentWillMount() {
    this.getPuzzle();
  }

  getPuzzle() {
    fetch("http://localhost:3000/puzzles", {
      method: "GET",
      headers: {
        "Content-Type": "application/json"
      }
    })
      .then(response => response.json()) //
      .then(responseJson => {
        this.setState({
          puzzle: responseJson.puzzles[0]
        });
        this.splitPuzzle();
        console.log(this.state.puzzle);
      })

      .catch(error => {
        console.error(error);
      });
  }

  splitPuzzle() {
    const columns = [[], [], [], [], [], [], [], [], []];

    columns[0].push(this.state.puzzle.puzzle.slice(0, 9));
    columns[1].push(this.state.puzzle.puzzle.slice(9, 18));
    columns[2].push(this.state.puzzle.puzzle.slice(18, 27));
    columns[3].push(this.state.puzzle.puzzle.slice(27, 36));
    columns[4].push(this.state.puzzle.puzzle.slice(36, 45));
    columns[5].push(this.state.puzzle.puzzle.slice(45, 54));
    columns[6].push(this.state.puzzle.puzzle.slice(54, 63));
    columns[7].push(this.state.puzzle.puzzle.slice(63, 72));
    columns[8].push(this.state.puzzle.puzzle.slice(72, 81));

    this.setState({
      columns: columns,
      isLoading: false
    });

    console.log(this.state.columns);
  }

  render() {
    return (
      <div className="appView">
        {this.state.isLoading ? (
          "loading"
        ) : (
          <GameBoard columns={this.state.columns} />
        )}
      </div>
    );
  }
}

export default App;
