            </button>
            <button
              style={
                this.state.difficulty === "hard"
                  ? { backgroundColor: "red" }
                  : { backgroundColor: "white" }
              }
              value="hard"
              className="actionBtn"
              onClick={this.chooseDifficulty}
            >
              HARD
            </button>
          </div>
        </div>
      </div>
    );
  }
  //styling

  cellColor(cellIndex) {
    for (let i = 0; i < this.state.wrongAnswers.length; i++) {
      if (cellIndex === this.state.wrongAnswers[i] + 1) {
        return { backgroundColor: "red" };
      }
    }
