import React, { Component } from "react";
import "./App.css";

class App extends Component {
  state = {
    puzzle: [],
    rows: [],
    solution: [],
    isLoading: true,
    selectedCell: null
  };

  componentWillMount() {
    this.getPuzzle();
  }

  getPuzzle() {
    fetch("http://localhost:3000/puzzles", {
      method: "GET",
      headers: {
        "Content-Type": "application/json"
      }
    })
      .then(response => response.json()) //
      .then(responseJson => {
        this.setState({
          puzzle: responseJson.puzzles[0],
          solution: responseJson.solution
        });
        this.splitPuzzle();
        console.log(this.state.puzzle);
      })

      .catch(error => {
        console.error(error);
      });
  }

  splitPuzzle() {
    const rows = [[], [], [], [], [], [], [], [], []];

    rows[0].push(this.state.puzzle.puzzle.slice(0, 9));
    rows[1].push(this.state.puzzle.puzzle.slice(9, 18));
    rows[2].push(this.state.puzzle.puzzle.slice(18, 27));
    rows[3].push(this.state.puzzle.puzzle.slice(27, 36));
    rows[4].push(this.state.puzzle.puzzle.slice(36, 45));
    rows[5].push(this.state.puzzle.puzzle.slice(45, 54));
    rows[6].push(this.state.puzzle.puzzle.slice(54, 63));
    rows[7].push(this.state.puzzle.puzzle.slice(63, 72));
    rows[8].push(this.state.puzzle.puzzle.slice(72, 81));

    this.setState({
      rows: rows,
      isLoading: false
    });

    console.log(this.state.rows);
  }

  renderCells(i) {
    let j = 0;
    return this.state.rows[i].map(row =>
      row.map(num => (
        <div className="cell" key={j++}>
          <button onClick={this.handleSelectedCell} className="cellBtn">
            {num}
          </button>
        </div>
      ))
    );
  }

  handleSelectedCell() {
    this.setState(state => ({
      isToggleOn: !state.isToggleOn
    }));
  }

  render() {
    return (
      <div className="appView">
        {this.state.isLoading ? (
          "loading"
        ) : (
          <div className="gameBoard">
            <div>{this.renderCells(0)}</div>
            <div>{this.renderCells(1)}</div>
            <div>{this.renderCells(2)}</div>
            <div>{this.renderCells(3)}</div>
            <div>{this.renderCells(4)}</div>
            <div>{this.renderCells(5)}</div>
            <div>{this.renderCells(6)}</div>
            <div>{this.renderCells(7)}</div>
            <div>{this.renderCells(8)}</div>
          </div>
        )}

        <div>
          <button>1</button>
          <button>2</button>
          <button>3</button>
          <button>4</button>
          <button>5</button>
          <button>6</button>
          <button>7</button>
          <button>8</button>
          <button>9</button>
        </div>
      </div>
    );
  }
}

export default App;
